    <div class="footer-copyright text-center py-3">Â© 2019 Copyright:
        <a href="#"> SureVote.com</a>
    </div>
    <!-- Copyright -->

    </footer>
    <!-- Footer -->

<style>
    #facebook,
    #twitter,
    #linkedin,
    #google,
